// Process Batch Compliance Agent output and format for Decision Agent
const output = [];

for (const item of $input.all()) {
  // Get the LLM agent output
  let agentOutput = item.json.output || item.json.text || item.json.response || null;

  // Try to parse JSON from LLM output (may be wrapped in markdown)
  let batchAssessment = {};
  if (agentOutput) {
    if (typeof agentOutput === 'string') {
      // Remove markdown code blocks if present
      agentOutput = agentOutput.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
      try {
        batchAssessment = JSON.parse(agentOutput);
      } catch (e) {
        // If parsing fails, create a default structure
        batchAssessment = {
          batchId: 'unknown',
          riskLevel: 'unknown',
          status: 'FAIL'
        };
      }
    } else if (typeof agentOutput === 'object') {
      batchAssessment = agentOutput;
    }
  }

  // Determine status based on risk level
  // FAIL if riskLevel is 'high', otherwise PASS
  const riskLevel = batchAssessment.riskLevel || 'low';
  const status = (riskLevel === 'high' || riskLevel === 'unacceptable') ? 'FAIL' : 'PASS';

  // Create Decision Agent compatible format
  const decisionInput = {
    agent: 'Batch Compliance Agent',
    status: status,
    batchId: batchAssessment.batchId || 'unknown',
    shipmentId: batchAssessment.shipmentId || 'unknown',
    riskLevel: riskLevel,
    confidence: batchAssessment.confidence || 'medium',
    topRisks: batchAssessment.topRisks || [],
    evidence: batchAssessment.evidence || [],
    suggestedFollowUps: batchAssessment.suggestedFollowUps || [],
    reasons: batchAssessment.topRisks && batchAssessment.topRisks.length > 0 
      ? batchAssessment.topRisks 
      : ['No significant batch compliance risks detected'],
    // Include full batch assessment for context
    fullBatchAssessment: batchAssessment
  };

  output.push({ json: decisionInput });
}

return output;

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  item.json.myNewField = 1;
}

return $input.all();

// Context + Compliance Builder (FIXED: supports batch, batches[], and shipment.batch)

// using supplierProfiles + warehouseProfiles from prior nodes
// Expects a prior node named "Context File" that outputs:
// { supplierProfiles, warehouseProfiles, getSupplierProfileEnabled: true }

const CONTEXT_NODE_NAME = "Context File";

// ---- Load context ----------------------------------------------------------
const ctxItem = $items(CONTEXT_NODE_NAME)?.[0]?.json ?? {};
const supplierProfiles = ctxItem.supplierProfiles ?? {};
const warehouseProfiles = ctxItem.warehouseProfiles ?? {};

// ---- Constants -------------------------------------------------------------
const KNOWN_CERTS = new Set([
  "GOTS","GRS","OEKO-TEX","Fair Trade","B Corp","ISO 14001","Cradle to Cradle",
  "RWS","FSC","PEFC","Bluesign","Organic","REACH","BCI",
]);

// ---- Helpers ---------------------------------------------------------------
function asArray(v) {
  return Array.isArray(v) ? v : [];
}

function uniq(arr) {
  return [...new Set(asArray(arr).filter((x) => x !== null && x !== undefined && x !== ""))];
}

// ---- Fallback profile generators ------------------------------------------
function getSupplierProfile(supplierId) {
  if (!supplierId || typeof supplierId !== "string") return null;
  if (supplierProfiles[supplierId]) return supplierProfiles[supplierId];

  const h = [...String(supplierId)].reduce((a, c) => (a * 31 + c.charCodeAt(0)) >>> 0, 7);
  const relationship = h % 3 === 0 ? "new" : h % 3 === 1 ? "limited" : "established";
  const issuePool = [
    "Low history increases uncertainty for provenance claims.",
    "Certification labels vary; confirm scope alignment.",
    "Potential documentation inconsistency across items.",
  ];

  return {
    relationship,
    history: "Profile not curated yet; generated placeholder based on supplier ID.",
    auditNotes: "No curated audit notes; treat confidence as low until enriched.",
    knownIssues: [issuePool[h % issuePool.length]],
    lastAudit: null,
    issuerTrustNotes: "No issuer registry linked; treat claims as unverified.",
  };
}

function getWarehouseProfile(warehouseLocation) {
  if (!warehouseLocation || typeof warehouseLocation !== "string") return null;
  return warehouseProfiles[warehouseLocation] ?? {
    chainOfCustodyMaturity: "medium",
    capacityNotes: "No curated warehouse profile; treat as unknown baseline.",
    incidentNotes: "No incident log present.",
    knownIssues: ["Unknown warehouse profile → higher uncertainty."],
    controls: ["Request chain-of-custody / operator ID evidence."],
  };
}

// ---- Certification logic ---------------------------------------------------
function gatherCertifications(dpp) {
  const raw = asArray(dpp?.rawMaterialsAndProcess?.certifications);
  const evals = asArray(dpp?.evaluations?.certifications);
  return uniq([...raw, ...evals]);
}

function certificationChecks(materialTypesObserved, certificationsObserved) {
  const flags = [];

  const unknownCerts = certificationsObserved.filter((c) => !KNOWN_CERTS.has(c));
  if (unknownCerts.length) {
    flags.push({
      code: "unknown_certification_label",
      detail: `Unknown certification labels: ${unknownCerts.join(", ")}`,
    });
  }

  const synthetic = new Set(["polyester","nylon","acrylic","elastane","spandex"]);
  if (certificationsObserved.includes("Organic")) {
    const syntheticsFound = materialTypesObserved
      .map((m) => String(m).toLowerCase())
      .filter((m) => synthetic.has(m));
    if (syntheticsFound.length) {
      flags.push({
        code: "cert_scope_mismatch_plausibility",
        detail: `Certification "Organic" observed alongside synthetic material types: ${uniq(syntheticsFound).join(", ")}.`,
      });
    }
  }

  return {
    certificationPresent: certificationsObserved.length > 0,
    unknownCertifications: unknownCerts,
    flags,
  };
}

// ---- Batch traversal -------------------------------------------------------
function garmentsInBatch(batch) {
  const orders = asArray(batch?.orders);
  const garments = [];
  for (const o of orders) for (const g of asArray(o?.garments)) garments.push(g);
  return garments;
}

function buildBatchComplianceContext(shipment, batch) {
  const garments = garmentsInBatch(batch);

  const suppliers = [];
  const warehouses = [];
  const materialTypes = [];
  const allCerts = [];

  for (const g of garments) {
    const dpp = g?.dpp;
    if (!dpp) continue;

    const supplier = dpp?.rawMaterialsAndProcess?.supplier ?? null;
    const warehouse = dpp?.distribution?.warehouseLocation ?? null;
    const materialType = dpp?.rawMaterialsAndProcess?.materialType ?? null;
    const certs = gatherCertifications(dpp);

    if (supplier) suppliers.push(supplier);
    if (warehouse) warehouses.push(warehouse);
    if (materialType) materialTypes.push(materialType);
    if (certs.length) allCerts.push(...certs);
  }

  const uniqSuppliers = uniq(suppliers);
  const uniqWarehouses = uniq(warehouses);
  const uniqMaterials = uniq(materialTypes);
  const uniqCerts = uniq(allCerts);

  const supplierContext = uniqSuppliers.map((s) => ({
    supplier: s,
    profile: getSupplierProfile(s),
    profileMissing: supplierProfiles[s] ? false : true,
  }));

  const warehouseContext = uniqWarehouses.map((w) => ({
    warehouseLocation: w,
    profile: getWarehouseProfile(w),
    profileMissing: warehouseProfiles[w] ? false : true,
  }));

  const checks = certificationChecks(uniqMaterials, uniqCerts);

  return {
    shipmentId: shipment?.shipmentId ?? batch?.shipmentId ?? null,
    batchId: batch?.batchId ?? null,
    actorContext: {
      suppliers: supplierContext,
      warehouses: warehouseContext,
    },
    documentationAndCertification: {
      materialsObserved: uniqMaterials,
      certificationsObserved: uniqCerts,
      certificationPresent: checks.certificationPresent,
      unknownCertifications: checks.unknownCertifications,
      flags: checks.flags,
    },
  };
}

// ---- MAIN ------------------------------------------------------------------
const items = $input.all();
const out = [];

for (const item of items) {
  const data = item.json;

  // Case A: input IS a batch
  if (data?.batchId && Array.isArray(data?.orders)) {
    const ctx = buildBatchComplianceContext({ shipmentId: data.shipmentId }, data);
    out.push({ json: { ...data, complianceContext: ctx } });
    continue;
  }

  // Case B: shipment with batches[]
  if (Array.isArray(data?.batches)) {
    for (const b of data.batches) {
      const ctx = buildBatchComplianceContext(data, b);
      out.push({
        json: {
          shipmentId: data.shipmentId,
          origin: data.origin,
          destination: data.destination,
          priority: data.priority,
          batch: b,
          complianceContext: ctx,
        },
      });
    }
    continue;
  }

  // Case C: shipment with single batch
  if (data?.batch && Array.isArray(data.batch?.orders)) {
    const ctx = buildBatchComplianceContext(data, data.batch);
    out.push({
      json: {
        ...data,
        complianceContext: ctx,
      },
    });
    continue;
  }

  // Fallback
  out.push({
    json: {
      ...data,
      complianceContext: { error: "Unsupported input shape." },
    },
  });
}

return out;

// --- Dijkstra & Helper Functions ---
const mathInf = Number.POSITIVE_INFINITY;

function buildGraph(edges) {
  const g = {};
  if (!edges) return g;
  for (const e of edges) {
    const from = e["from"];
    const to = e["to"];
    if (!g[from]) g[from] = {};
    g[from][to] = {
      distanceKm: Number(e["distanceKm"]),
      timeHours: Number(e["timeHours"]),
      costEUR: Number(e["costEUR"]),
    };
  }
  return g;
}

class MinHeap {
  constructor() { this.a = []; }
  size() { return this.a.length; }
  push(item) {
    this.a.push(item);
    this._siftUp(this.a.length - 1);
  }
  pop() {
    if (this.a.length === 0) return null;
    const top = this.a[0];
    const last = this.a.pop();
    if (this.a.length > 0) {
      this.a[0] = last;
      this._siftDown(0);
    }
    return top;
  }
  _siftUp(i) {
    while (i > 0) {
      const p = Math.floor((i - 1) / 2);
      if (this.a[p][0] <= this.a[i][0]) break;
      [this.a[p], this.a[i]] = [this.a[i], this.a[p]];
      i = p;
    }
  }
  _siftDown(i) {
    const n = this.a.length;
    while (true) {
      let smallest = i;
      const l = 2 * i + 1;
      const r = 2 * i + 2;
      if (l < n && this.a[l][0] < this.a[smallest][0]) smallest = l;
      if (r < n && this.a[r][0] < this.a[smallest][0]) smallest = r;
      if (smallest === i) break;
      [this.a[i], this.a[smallest]] = [this.a[smallest], this.a[i]];
      i = smallest;
    }
  }
}

function dijkstra(graph, origin, destination, weightKey) {
  const pq = new MinHeap();
  pq.push([0.0, origin, [origin]]);
  const visited = new Set();
  while (pq.size() > 0) {
    const [cost, node, path] = pq.pop();
    if (node === destination) return [cost, path];
    if (visited.has(node)) continue;
    visited.add(node);
    const nbrs = graph[node] || {};
    for (const [nxt, w] of Object.entries(nbrs)) {
      pq.push([cost + Number(w[weightKey]), nxt, path.concat([nxt])]);
    }
  }
  return [mathInf, []];
}

function sumAlongPath(graph, path) {
  let dist = 0.0, time = 0.0, cost = 0.0;
  for (let i = 0; i < path.length - 1; i++) {
    const w = graph[path[i]][path[i+1]];
    dist += w.distanceKm;
    time += w.timeHours;
    cost += w.costEUR;
  }
  return [dist, time, cost];
}

// --- Main n8n Logic ---

// Access the merged input item
const input = $input.first().json;

// Extract shipments - handle both direct shipments array and merged structure
let shipmentsList = [];
if (input.shipments && Array.isArray(input.shipments)) {
  shipmentsList = input.shipments;
} else if (Array.isArray(input) && input.length > 0 && input[0].shipments) {
  shipmentsList = input[0].shipments;
} else if (input.shipmentId) {
  // Single shipment object
  shipmentsList = [input];
}

// Access edges from the network data part of the merge
const edges = input.edges || []; 
const graph = buildGraph(edges);

const results = shipmentsList.map(sh => {
  const origin = sh.origin;
  const destination = sh.destination;
  const priority = sh.priority;

  const prNorm = ((priority ?? "low") + "").toLowerCase();
  const weightKey = (prNorm === "express" || prNorm === "high") ? "timeHours" : "costEUR";

  const [totalWeight, path] = dijkstra(graph, origin, destination, weightKey);

  // Initialize structure
  let distanceKm = 0, timeHours = 0, costEUR = 0;

  if (path && path.length > 1) {
    const totals = sumAlongPath(graph, path);
    distanceKm = Number(totals[0].toFixed(1));
    timeHours = Number(totals[1].toFixed(1));
    costEUR = Number(totals[2].toFixed(1));
  }

  return {
    shipmentId: sh.shipmentId || "UNKNOWN",
    origin: origin || sh.origin || "",
    destination: destination || sh.destination || "",
    route: path,
    distanceKm,
    timeHours,
    costEUR,
    priority: priority || sh.priority || "Medium",
    logisticsNetwork: {
      edges: path.length > 1 ? [{
        from: origin,
        to: destination,
        timeHours,
        costEUR
      }] : []
    },
    batches: sh.batches || []
  };
});

return {
  json: {
    shipments: results
  }
};

function isMissing(v) {
  if (v === null || v === undefined) return true;
  if (typeof v === "string" && v.trim() === "") return true;
  if (Array.isArray(v) && v.length === 0) return true;
  return false;
}

const allowNullPathPrefixes = [
  "batches[].orders[].garments[].dpp.collection.collectionPartner",
  "batches[].orders[].garments[].dpp.recycling.recyclingProcess",
];

const REQUIRED = {
  shipment: ["shipmentId", "origin", "destination", "route", "distanceKm", "timeHours", "costEUR", "priority", "batches"],
  batch: ["batchId", "shipmentId", "timestamp", "orders"],
  order: ["id", "batchId", "brand", "quantity", "garments"],
  garment: ["id", "orderId", "dpp"],
  dpp: [
    "uuid",
    "rawMaterialsAndProcess",
    "rawMaterialsConversion",
    "component",
    "productAssembly",
    "finishedProduct",
    "distribution",
    "usage",
    "afterSale",
    "collection",
    "recycling",
    "endOfLife",
    "transports",
    "evaluations",
  ],
};

function pathAllowedNull(path) {
  return allowNullPathPrefixes.some((p) => path.startsWith(p));
}

function addIssue(issues, path, reason, value) {
  if (value === null && pathAllowedNull(path)) return;
  issues.push({ path, reason, value });
}

function checkObjectRequired(obj, requiredKeys, basePath, issues) {
  for (const key of requiredKeys) {
    const v = obj?.[key];
    const p = `${basePath}.${key}`;
    if (isMissing(v)) addIssue(issues, p, "missing/empty", v);
  }
}

function computeIssuesForItem(data) {
  const shipments = Array.isArray(data) ? data : [data];
  const issues = [];

  shipments.forEach((s, si) => {
    const sPath = Array.isArray(data) ? `[${si}]` : "shipment";

    checkObjectRequired(s, REQUIRED.shipment, sPath, issues);

    if (Array.isArray(s?.route)) {
      s.route.forEach((r, ri) => {
        if (isMissing(r)) addIssue(issues, `${sPath}.route[${ri}]`, "missing/empty", r);
      });
    }

    (s?.batches || []).forEach((b, bi) => {
      const bPath = `${sPath}.batches[${bi}]`;
      checkObjectRequired(b, REQUIRED.batch, bPath, issues);

      (b?.orders || []).forEach((o, oi) => {
        const oPath = `${bPath}.orders[${oi}]`;
        checkObjectRequired(o, REQUIRED.order, oPath, issues);

        (o?.garments || []).forEach((g, gi) => {
          const gPath = `${oPath}.garments[${gi}]`;
          checkObjectRequired(g, REQUIRED.garment, gPath, issues);

          const dpp = g?.dpp;
          const dppPath = `${gPath}.dpp`;
          if (isMissing(dpp)) {
            addIssue(issues, dppPath, "missing/empty", dpp);
            return;
          }

          checkObjectRequired(dpp, REQUIRED.dpp, dppPath, issues);

          const nested = [
            { obj: dpp.rawMaterialsAndProcess, keys: ["sourceCountry", "materialType", "supplier", "harvestDate", "certifications"], path: `${dppPath}.rawMaterialsAndProcess` },
            { obj: dpp.rawMaterialsConversion, keys: ["processingFacility", "location", "processDate", "processType"], path: `${dppPath}.rawMaterialsConversion` },
            { obj: dpp.finishedProduct, keys: ["productId", "completionDate", "qualityGrade"], path: `${dppPath}.finishedProduct` },
            { obj: dpp.evaluations, keys: ["esgRating", "certifications", "lastAuditDate", "complianceScore"], path: `${dppPath}.evaluations` },
          ];

          nested.forEach(({ obj, keys, path }) => {
            if (isMissing(obj)) {
              addIssue(issues, path, "missing/empty", obj);
              return;
            }
            keys.forEach((k) => {
              const v = obj[k];
              const p = `${path}.${k}`;
              if (isMissing(v)) addIssue(issues, p, "missing/empty", v);
            });
          });
        });
      });
    });
  });

  return issues;
}

return $input.all().map((item, idx) => {
  const issues = computeIssuesForItem(item.json);
  return {
    json: {
      ...item.json, // original data stays here
      _missingCheck: {
        ok: issues.length === 0,
        missingCount: issues.length,
        missing: issues.slice(0, 200),
      },
    },
  };
});

// Includes:
// - warehouseProfiles for all warehouseLocation values in your dataset
// - example supplierProfiles for suppliers explicitly present in your dataset
// - a fallback generator for any supplier not listed (so you don’t need 50+ manual entries)
//
// Supplier field appears at dpp.rawMaterialsAndProcess.supplier :contentReference[oaicite:0]{index=0}
// Warehouse field appears at dpp.distribution.warehouseLocation :contentReference[oaicite:1]{index=1}

