// Fallback profile generator for any supplier not explicitly listed above.
// Keeps profiles varied without hand-writing 50+ entries.
function getSupplierProfile(supplierId) {
  if (supplierProfiles[supplierId]) return supplierProfiles[supplierId];

  // Simple stable hash → pick “relationship” and one issue.
  const h = [...String(supplierId)].reduce((a, c) => (a * 31 + c.charCodeAt(0)) >>> 0, 7);
  const relationship = h % 3 === 0 ? "new" : h % 3 === 1 ? "limited" : "established";
  const issuePool = [
    "Low history increases uncertainty for provenance claims.",
    "Certification labels vary; confirm scope alignment.",
    "Potential documentation inconsistency across items."
  ];

  return {
    relationship,
    history: "Profile not curated yet; generated placeholder based on supplier ID.",
    auditNotes: "No curated audit notes; treat confidence as low until enriched.",
    knownIssues: [issuePool[h % issuePool.length]],
    lastAudit: null,
    issuerTrustNotes: "No issuer registry linked; treat claims as unverified."
  };
}

// Export these from the node if you want them in your context block:
return [
  {
    json: {
      supplierProfiles,
      warehouseProfiles,
      getSupplierProfileEnabled: true
    }
  }
];

// Input item shape: { shipments: { ... , batches: [ ... ] } }
const s = $json.shipments;
const batches = s.batches ?? [];

return batches.map(b => ({
  json: {
    // keep shipment context (useful for routing/compliance decisions)
    shipmentId: s.shipmentId,
    origin: s.origin,
    destination: s.destination,
    route: s.route,
    distanceKm: s.distanceKm,
    timeHours: s.timeHours,
    costEUR: s.costEUR,
    priority: s.priority,
    logisticsNetwork: s.logisticsNetwork,

    // the batch you want to loop over
    batch: b,
    batchId: b.batchId,
  }
}));

// Prepare response data based on validation status
const items = $input.all();
const output = [];

for (const item of items) {
  const data = item.json;
  const missingCheck = data._missingCheck || {};

  if (missingCheck.ok === false) {
    // Data is missing
    const missingFields = (missingCheck.missing || []).map(m => m.path || m);
    output.push({
      json: {
        status: "error",
        message: "Data is missing. Please re-upload the file with all required fields.",
        missing_fields: missingFields,
        missing_count: missingCheck.missingCount || missingFields.length,
        responseCode: 400
      }
    });
  } else {
    // Data is complete
    output.push({
      json: {
        status: "success",
        message: "Upload successful! Data is complete. Processing has started.",
        responseCode: 200
      }
    });
  }
}

return output;

// Process LLM output and combine with original shipment data to create comprehensive compliance JSON
// Input 0: LLM agent output
// Input 1: Original shipment data with weather from Combine Weather & Shipments
const output = [];

// Separate LLM outputs from original shipment data
const llmOutputs = [];
const shipmentDataItems = [];

for (const item of $input.all()) {
  // Check if it's LLM output (has 'output' or 'text' field from agent)
  if (item.json.output || item.json.text || (item.json.response && !item.json.shipments)) {
    llmOutputs.push(item);
  }
  // Check if it's original shipment data (has 'shipments' property)
  else if (item.json.shipments) {
    shipmentDataItems.push(item);
  }
}

// Process each shipment
for (let i = 0; i < Math.max(llmOutputs.length, shipmentDataItems.length); i++) {
  const llmItem = llmOutputs[i] || llmOutputs[0];
  const shipmentItem = shipmentDataItems[i] || shipmentDataItems[0];

  // Get the LLM agent output
  let agentOutput = llmItem?.json.output || llmItem?.json.text || llmItem?.json.response || null;

  // Get original shipment data
  const shipmentData = shipmentItem?.json.shipments || shipmentItem?.json || {};

  // Try to parse JSON from LLM output (may be wrapped in markdown)
  let complianceAssessment = {};
  if (agentOutput) {
    if (typeof agentOutput === 'string') {
      // Remove markdown code blocks if present
      agentOutput = agentOutput.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
      try {
        complianceAssessment = JSON.parse(agentOutput);
      } catch (e) {
        // If parsing fails, create a default structure
        complianceAssessment = {
          shipmentId: shipmentData?.shipmentId || 'unknown',
          routeRisk: 'unknown',
          score: 1.0,
          riskExplanation: 'LLM output could not be parsed: ' + e.message,
          issues: ['Failed to parse LLM response'],
          recommendations: {}
        };
      }
    } else if (typeof agentOutput === 'object') {
      complianceAssessment = agentOutput;
    }
  }

  // Create comprehensive compliance output
  const complianceOutput = {
    // Shipment identification
    shipmentId: complianceAssessment.shipmentId || shipmentData?.shipmentId || 'unknown',
    timestamp: new Date().toISOString(),

    // Original shipment data
    shipment: {
      shipmentId: shipmentData?.shipmentId || complianceAssessment.shipmentId || 'unknown',
      origin: shipmentData?.origin || null,
      destination: shipmentData?.destination || null,
      route: shipmentData?.route || [],
      priority: shipmentData?.priority || null,
      distanceKm: shipmentData?.distanceKm || null,
      timeHours: shipmentData?.timeHours || null,
      costEUR: shipmentData?.costEUR || null,
      batches: shipmentData?.batches || [],
      batchIds: shipmentData?.batchIds || [],
      batchCount: shipmentData?.batchCount || 0
    },

    // Weather information
    weather: {
      overallRisk: shipmentData?.weatherRisk || complianceAssessment.weatherRisk || 'unknown',
      hasSevereWeather: shipmentData?.hasSevereWeather || false,
      weatherIssues: shipmentData?.weatherIssues || [],
      routeWeatherDetails: shipmentData?.routeWeatherDetails || [],
      summary: shipmentData?.weatherSummary || {
        totalCities: 0,
        citiesWithWeather: 0,
        highRiskCities: [],
        unacceptableRiskCities: []
      }
    },

    // Compliance assessment from LLM
    compliance: {
      routeRisk: complianceAssessment.routeRisk || 'unknown',
      score: complianceAssessment.score !== undefined ? complianceAssessment.score : 1.0,
      riskExplanation: complianceAssessment.riskExplanation || complianceAssessment.context || 'No explanation provided',
      issues: complianceAssessment.issues || [],

      // LLM recommendations
      recommendations: complianceAssessment.recommendations || {
        batchActions: [],
        routeChanges: [],
        timingAdjustments: [],
        priorityConsiderations: null
      },

      // Affected ports and alternatives
      affectedPorts: complianceAssessment.affectedPorts || [],
      alternativeRoutes: complianceAssessment.alternativeRoutes || []
    },

    // Metadata
    metadata: {
      assessmentDate: new Date().toISOString(),
      agentVersion: '1.0',
      dataCompleteness: {
        hasWeatherData: (shipmentData?.routeWeatherDetails?.length || 0) > 0,
        hasRouteData: (shipmentData?.route?.length || 0) > 0,
        hasBatchData: (shipmentData?.batchIds?.length || 0) > 0,
        hasLLMOutput: !!agentOutput
      }
    }
  };

  output.push({ json: complianceOutput });
}

return output;

// Create final comprehensive compliance JSON output for Decision Agent
// This node receives the processed agent result which contains both LLM output and shipment data
const output = [];

for (const item of $input.all()) {
  // The Process Route Agent Result already created the compliance structure
  // Just ensure it's properly formatted
  const complianceData = item.json;

  // Validate and ensure all required fields are present
  const finalOutput = {
    // Shipment identification
    shipmentId: complianceData.shipmentId || 'unknown',
    timestamp: complianceData.timestamp || new Date().toISOString(),

    // Original shipment data
    shipment: complianceData.shipment || {},

    // Weather information
    weather: complianceData.weather || {},

    // Compliance assessment from LLM
    compliance: complianceData.compliance || {},

    // Metadata
    metadata: complianceData.metadata || {}
  };

  output.push({ json: finalOutput });
}

return output;

// Transform route compliance to structured format for Decision Agent
const output = [];

for (const item of $input.all()) {
  const routeData = item.json;

  // Extract shipment info
  const shipment = routeData.shipment || {};
  const shipmentId = shipment.shipmentId || routeData.shipmentId || 'unknown';

  // Extract route compliance assessment
  const routeAssessment = routeData.routeAssessment || {};
  const routeRisk = routeAssessment.routeRisk || routeAssessment.overallRisk || 'unknown';

  // Extract weather data
  const weatherData = routeData.weatherData || shipment.routeWeatherDetails || [];
  const overallWeatherRisk = routeData.overallWeatherRisk || shipment.overallWeatherRisk || 'unknown';

  // Extract structured data for affected ports
  const affectedPorts = weatherData
    .filter(w => w.weatherRisk === 'high' || w.weatherRisk === 'unacceptable')
    .map(w => w.city);

  // Determine status
  const status = (routeRisk === 'unacceptable' || overallWeatherRisk === 'unacceptable') 
    ? 'FAIL' 
    : 'PASS';

  // Extract recommendations and issues
  const recommendations = routeAssessment.recommendations || [];
  const issues = routeAssessment.issues || [];
  const alternativeRoutes = routeAssessment.alternativeRoutes || [];

  // Extract batch IDs
  const batchIds = (shipment.batches || []).map(b => b.batchId);

  output.push({
    json: {
      agent: 'Route Compliance Agent',
      shipmentId: shipmentId,
      batchIds: batchIds,
      status: status,
      routeRisk: routeRisk,
      weatherRisk: overallWeatherRisk,
      affectedPorts: affectedPorts,
      recommendations: recommendations,
      issues: issues,
      alternativeRoutes: alternativeRoutes
    }
  });
}

return output;

// Filter and prepare shipment data for route extraction
const output = [];

for (const item of $input.all()) {
  // Handle case where shipments is an array (from Code in JavaScript1)
  let shipmentsToProcess = [];

  if (Array.isArray(item.json.shipments)) {
    // If shipments is an array, process each shipment
    shipmentsToProcess = item.json.shipments;
  } else if (item.json.shipments && item.json.shipments.shipmentId) {
    // If shipments is a single object
    shipmentsToProcess = [item.json.shipments];
  } else if (item.json.shipmentId) {
    // If the item itself is a shipment
    shipmentsToProcess = [item.json];
  } else {
    continue;
  }

  // Process each shipment
  for (const shipment of shipmentsToProcess) {
    if (!shipment || !shipment.shipmentId) continue;

    const filteredBatches = (shipment.batches || []).map(batch => ({
      batchId: batch.batchId,
      orders: (batch.orders || []).map(order => ({
        id: order.id,
        garments: (order.garments || []).map(garment => ({
          id: garment.id,
          dpp: {
            transports: garment.dpp?.transports ?? null,
            evaluations: garment.dpp?.evaluations ?? null
          }
        }))
      }))
    }));

    output.push({
      json: {
        shipments: {
          shipmentId: shipment.shipmentId,
          origin: shipment.origin,
          destination: shipment.destination,
          route: shipment.route || [],
          priority: shipment.priority,
          distanceKm: shipment.distanceKm || null,
          timeHours: shipment.timeHours || null,
          costEUR: shipment.costEUR || null,
          batches: filteredBatches
        }
      }
    });
  }
}

return output;

// Parse Nominatim API response and extract coordinates
// CRITICAL: City name MUST be preserved, especially when coordinates are present
const output = [];

for (const item of $input.all()) {
  // CRITICAL: Preserve city name - try multiple locations
  // HTTP Request node might preserve original data in different locations
  let city = item.json.city || '';

  // Check if city is in a nested structure (n8n sometimes wraps data)
  if (!city && item.json.json) {
    if (typeof item.json.json === 'object' && !Array.isArray(item.json.json)) {
      city = item.json.json.city || '';
    }
  }

  // Nominatim API returns a JSON array - check multiple possible locations
  let results = null;

  // Check if response is directly an array (raw API response)
  if (Array.isArray(item.json)) {
    results = item.json;
  }
  // Check if response is in body (n8n HTTP Request node format)
  else if (item.json.body) {
    if (Array.isArray(item.json.body)) {
      results = item.json.body;
    } else if (typeof item.json.body === 'string') {
      // Try to parse if it's a string
      try {
        const parsed = JSON.parse(item.json.body);
        if (Array.isArray(parsed)) {
          results = parsed;
        }
      } catch (e) {
        // Parsing failed
        results = null;
      }
    }
  }
  // Check if response is already parsed in json property
  else if (item.json.json && Array.isArray(item.json.json)) {
    results = item.json.json;
  }
  // Check if it's a single object with lat/lon (wrap in array)
  else if (item.json.lat && item.json.lon) {
    results = [item.json];
    // Extract city from display_name if available
    if (!city && item.json.display_name) {
      city = item.json.display_name.split(',')[0].trim();
    }
  }

  // If we still don't have a city, try to extract from results
  if (!city && results && Array.isArray(results) && results.length > 0) {
    const firstResult = results[0];
    if (firstResult.display_name) {
      // Extract city name from display_name (format: "City, Region, Country")
      city = firstResult.display_name.split(',')[0].trim();
    }
  }

  // Extract first result's coordinates
  if (results && Array.isArray(results) && results.length > 0) {
    const firstResult = results[0];
    const lat = parseFloat(firstResult.lat) || null;
    const lon = parseFloat(firstResult.lon) || null;
    const displayName = firstResult.display_name || city || 'Unknown';

    // CRITICAL: City name MUST be present alongside coordinates
    // Use city from input, or extract from display_name if not available
    const finalCity = city || displayName.split(',')[0].trim() || 'Unknown';

    output.push({
      json: {
        city: finalCity, // Always preserve city name
        latitude: lat,
        longitude: lon,
        lat: lat, // Also include for weather API compatibility
        lon: lon,
        name: displayName,
        displayName: displayName,
        coordinatesFound: lat !== null && lon !== null
      }
    });
  } else {
    // No coordinates found - but still preserve city name if we have it
    // If no city, we still need to output something to keep the pipeline flowing
    const finalCity = city || 'Unknown';
    output.push({
      json: {
        city: finalCity, // Always preserve city name
        latitude: null,
        longitude: null,
        lat: null,
        lon: null,
        name: finalCity,
        displayName: finalCity,
        coordinatesFound: false,
        error: city && city !== 'Unknown' ? "No coordinates found for city: " + city : "No city name or coordinates found in response"
      }
    });
  }
}

// Always return output, even if empty (shouldn't happen, but safety check)
if (output.length === 0) {
  // This should not happen, but if it does, return a placeholder
  output.push({
    json: {
      city: 'Unknown',
      latitude: null,
      longitude: null,
      lat: null,
      lon: null,
      name: 'Unknown',
      displayName: 'Unknown',
      coordinatesFound: false,
      error: "No input data received"
    }
  });
}

return output;

// Parse weather API response and add risk assessment
// CRITICAL: City name MUST be preserved with weather data
const output = [];

for (const item of $input.all()) {
  // CRITICAL: Preserve city name - try multiple locations
  // HTTP Request node might preserve original data in different locations
  let city = item.json.city || '';

  // Check if city is in a nested structure (n8n sometimes wraps data)
  if (!city && item.json.json) {
    if (typeof item.json.json === 'object' && !Array.isArray(item.json.json)) {
      city = item.json.json.city || '';
    }
  }

  // Also check query parameters (city might be in the original request)
  if (!city && item.json.query) {
    city = item.json.query.city || '';
  }

  const latitude = item.json.latitude || item.json.lat || null;
  const longitude = item.json.longitude || item.json.lon || null;
  const coordinatesFound = item.json.coordinatesFound !== false;

  // If still no city but we have coordinates, try to preserve from previous step
  // This should not happen if Parse Coordinates worked correctly, but safety check
  if (!city) {
    // If we have coordinates, we should have had a city - mark as unknown
    if (latitude && longitude) {
      city = 'Unknown';
    } else {
      // Skip items without city name and without coordinates
      continue;
    }
  }

  // Parse weather data from API response
  let daily = {};
  if (item.json.daily) {
    daily = item.json.daily;
  } else if (item.json.body && item.json.body.daily) {
    daily = item.json.body.daily;
  }

  const times = daily.time || [];
  const weatherCodes = daily.weather_code || [];
  const precipitation = daily.precipitation_sum || [];

  // Calculate weather risk
  let maxPrecipitation = 0;
  let severeWeatherDays = 0;
  let weatherRisk = 'low';
  const weatherIssues = [];

  // Weather code meanings: 71-77 = snow, 80-82 = rain showers, 95-99 = thunderstorms
  const severeWeatherCodes = [71, 72, 73, 74, 75, 76, 77, 80, 81, 82, 95, 96, 97, 98, 99];

  for (let i = 0; i < times.length; i++) {
    const precip = precipitation[i] || 0;
    const code = weatherCodes[i] || 0;

    maxPrecipitation = Math.max(maxPrecipitation, precip);

    if (severeWeatherCodes.includes(code)) {
      severeWeatherDays++;
    }

    // High precipitation threshold: >50mm per day
    if (precip > 50) {
      weatherIssues.push(`High precipitation (${precip.toFixed(1)}mm) on ${times[i]}`);
    }
  }

  // Determine overall weather risk
  if (maxPrecipitation > 100 || severeWeatherDays > 2) {
    weatherRisk = 'unacceptable';
  } else if (maxPrecipitation > 50 || severeWeatherDays > 0) {
    weatherRisk = 'high';
  } else if (maxPrecipitation > 25) {
    weatherRisk = 'medium';
  }

  // If coordinates not found or weather API failed, mark as error
  if (!coordinatesFound || !daily || times.length === 0) {
    weatherRisk = 'unknown';
    weatherIssues.push('Weather data unavailable - coordinates or API error');
  }

  // CRITICAL: City name MUST be present with weather data for lookup
  output.push({
    json: {
      city: city, // Always preserve city name
      latitude: latitude,
      longitude: longitude,
      coordinatesFound: coordinatesFound,
      weatherData: {
        times: times,
        weatherCodes: weatherCodes,
        precipitation: precipitation,
        maxPrecipitation: maxPrecipitation,
        severeWeatherDays: severeWeatherDays,
        weatherRisk: weatherRisk,
        weatherIssues: weatherIssues
      },
      daily: daily // Keep original for compatibility
    }
  });
}

return output;

// Extract unique cities from current shipment route (from loop iteration)
// CRITICAL: Preserve shipmentId throughout the weather pipeline
const output = [];
const uniqueCities = new Set();

// Process the current loop iteration item (single shipment)
for (const item of $input.all()) {
  // Handle both single shipment object and array cases
  let shipment = null;

  if (item.json.shipments) {
    if (Array.isArray(item.json.shipments)) {
      shipment = item.json.shipments[0]; // Should be single shipment from loop
    } else if (item.json.shipments.shipmentId) {
      shipment = item.json.shipments;
    }
  } else if (item.json.shipmentId) {
    shipment = item.json;
  }

  if (!shipment) continue;

  // CRITICAL: Preserve shipmentId
  const shipmentId = shipment.shipmentId;

  // Extract route array from shipment
  const route = shipment.route || [];

  // Process each city in the route - add to unique set
  if (Array.isArray(route)) {
    for (const city of route) {
      if (typeof city === 'string' && city.trim() !== '') {
        uniqueCities.add(city.trim());
      }
    }
  }

  // Convert set to array of cities with shipmentId preserved
  const routeCities = Array.from(uniqueCities).map(city => ({ 
    city,
    shipmentId // CRITICAL: Preserve for entire pipeline
  }));

  output.push({ 
    json: { 
      routeCities,
      shipmentId // Also preserve at top level
    } 
  });
}

return output;

// Split routeCities array into individual items
// CRITICAL: Preserve both city name AND shipmentId at every step
const output = [];

for (const item of $input.all()) {
  const routeCities = item.json.routeCities || [];
  const shipmentId = item.json.shipmentId; // Get shipmentId from parent

  if (Array.isArray(routeCities)) {
    for (const cityData of routeCities) {
      const city = cityData.city || (typeof cityData === 'string' ? cityData : '');
      const cityShipmentId = cityData.shipmentId || shipmentId; // Preserve shipmentId

      if (city) {
        output.push({
          json: {
            city: city,
            shipmentId: cityShipmentId // CRITICAL: Pass shipmentId forward
          }
        });
      }
    }
  }
}

return output;

// Flatten weather data for export - one row per city per date
// Weather pipeline now works with city-only data (no shipmentId)
const rows = [];

for (const item of $input.all()) {
  // CRITICAL: City name is always present in weather data
  const city = item.json.city || '';
  const lat = item.json.latitude || item.json.lat || null;
  const lon = item.json.longitude || item.json.lon || null;

  if (!city) {
    // Skip items without city name
    continue;
  }

  // Get weather data
  const weatherData = item.json.weatherData || {};
  const daily = item.json.daily || {};
  const times = weatherData.times || daily.time || [];
  const weatherCodes = weatherData.weatherCodes || daily.weather_code || [];
  const precipitation = weatherData.precipitation || daily.precipitation_sum || [];
  const weatherRisk = weatherData.weatherRisk || 'unknown';
  const maxPrecipitation = weatherData.maxPrecipitation || 0;
  const severeWeatherDays = weatherData.severeWeatherDays || 0;
  const weatherIssues = weatherData.weatherIssues || [];

  // If no time data, create a single row with summary
  if (times.length === 0) {
    rows.push({
      json: {
        City: city,
        Latitude: lat,
        Longitude: lon,
        Date: 'N/A',
        WeatherCode: null,
        PrecipitationMM: null,
        WeatherRisk: weatherRisk,
        MaxPrecipitation: maxPrecipitation,
        SevereWeatherDays: severeWeatherDays,
        WeatherIssues: weatherIssues.join('; '),
        CoordinatesFound: item.json.coordinatesFound !== false
      }
    });
  } else {
    // Create one row per date for this city
    for (let i = 0; i < times.length; i++) {
      rows.push({
        json: {
          City: city,
          Latitude: lat,
          Longitude: lon,
          Date: times[i],
          WeatherCode: weatherCodes[i] ?? null,
          PrecipitationMM: precipitation[i] ?? null,
          WeatherRisk: weatherRisk,
          MaxPrecipitation: maxPrecipitation,
          SevereWeatherDays: severeWeatherDays,
          WeatherIssues: weatherIssues.join('; '),
          CoordinatesFound: item.json.coordinatesFound !== false
        }
      });
    }
  }
}

return rows;

