// Extract webhook body and format for merge
// Webhook receives JSON - it may be in body field or directly in json
const items = $input.all();
const output = [];

for (const item of items) {
  // n8n webhook puts POST body in 'body' field, but check both locations
  let data = item.json.body || item.json;

  // Handle different input formats
  if (data && typeof data === 'object') {
    // If it already has shipments array (like syntheticdata.json), use it
    if (data.shipments && Array.isArray(data.shipments)) {
      output.push({ json: data });
    }
    // If it's an array of shipments, wrap it
    else if (Array.isArray(data)) {
      output.push({ json: { shipments: data } });
    }
    // If it's a single shipment object, wrap in array
    else if (data.shipmentId || data.batchId) {
      output.push({ json: { shipments: [data] } });
    }
    // Otherwise pass through as-is (might already be correct format)
    else {
      output.push({ json: data });
    }
  } else {
    // Fallback: wrap in shipments array
    output.push({ json: { shipments: [data] } });
  }
}

return output;

// Split data into shipments (for routing) and batches (for compliance)
const items = $input.all();
const output = [];

for (const item of items) {
  const data = item.json;

  // Extract shipments array
  const shipments = data.shipments || [];

  // Extract batches - could be in shipments[].batches or separate batches array
  let allBatches = [];
  if (data.batches && Array.isArray(data.batches)) {
    allBatches = data.batches;
  } else if (shipments.length > 0 && shipments[0].batches) {
    allBatches = shipments[0].batches;
  }

  // Output shipments for routing
  output.push({
    json: {
      shipments: shipments,
      type: "routing"
    }
  });

  // Output batches for compliance
  output.push({
    json: {
      batches: allBatches,
      type: "compliance"
    }
  });
}

return output;

// Extract shipments for routing solver and routing agent
const items = $input.all();
const output = [];

for (const item of items) {
  if (item.json.type === "routing") {
    output.push({
      json: {
        shipments: item.json.shipments || []
      }
    });
  }
}

return output;

// Extract batches for compliance agent (after missing data check)
const items = $input.all();
const output = [];

for (const item of items) {
  if (item.json.type === "compliance") {
    // Structure batches for missing data check
    const batches = item.json.batches || [];

    // If it's a single batch object, wrap in array
    if (!Array.isArray(batches) && batches.batchId) {
      output.push({
        json: {
          batches: [batches]
        }
      });
    } else if (Array.isArray(batches)) {
      output.push({
        json: {
          batches: batches
        }
      });
    } else {
      output.push({
        json: {
          batches: [batches]
        }
      });
    }
  }
}

return output;
